const { visualizeTiles } = require('./services/googleAIService');
const fs = require('fs');
require('dotenv').config();

const smallJpeg = "";

async function runTest() {
    console.log("Starting visualization test with valid 1x1 JPEG...");
    const buffer = Buffer.from(smallJpeg, 'base64');

    try {
        // We pass the same tiny image for both room and tile
        // Expecting either a successful generation (even if garbage output)
        // OR a specific model error about image size/quality, which confirms AUTH works.
        const result = await visualizeTiles(buffer, buffer, 'image/jpeg', 'image/jpeg');
        console.log("SUCCESS: Got result string of length " + result.length);
    } catch (error) {
        console.error("FAILURE: " + error.message);
        if (error.response) {
            console.error("Response data:", JSON.stringify(error.response.data));
        }
    }
}

runTest();
